import React from 'react';
import './stil.css';
import axios from 'axios';
import { Button, Form, Card} from "react-bootstrap";
class Home extends React.Component {
  constructor(props){
    super(props)
    this.state= {
      searchBar: '',
      opis: '',
      uspjeh: ''
    };
  } 

  handleInputChange = (event) => {
    event.preventDefault()
      this.setState({
        [event.target.name] : event.target.value
      });
  };
  handleClick = (e) => {
    e.preventDefault();
    axios.get("http://localhost:5000/sinonimi/" + this.state.searchBar).then((res)=> {
      alert("Pronadjen");
      alert(res.data);
        this.setState({
            opis: "Pronađen.",
            uspjeh: res.data.synonym,
            searchBar: ""
        });
    })
    .catch(()=> {
        this.setState({ 
            opis: "Nije pronađen."
        });
    });
}

   render() {
    return (
      <div className="main-div">
          <Card style={{ width: '55rem' }} id="prva">
              <Card.Body>
                <Card.Title id="naslov"> Welcome to the  <br></br>synonyms search tool </Card.Title>
                <Card.Text id="tekst">
                  Search synonyms
                </Card.Text>
                  <Form>
                    <input type="text" placeholder="Enter a word" id="bar" name= "searchBar"  onChange= {this.handleInputChange}/>
                    <Button onClick={this.handleClick} variant="outline-warning">Search</Button>
                 
                  </Form>
                  <Button href="/addsynonym"variant="outline-warning" id="add"> Add your synonyms </Button>
              </Card.Body>
            </Card>
      </div>
    );
  }
}
export default Home;
