import React from 'react';
import './stil.css';
import { Button, Form,  Card} from "react-bootstrap";
import axios from 'axios';
class AddSynonym extends React.Component {
  constructor(props){
    super(props);
    this.state={
      persons: [],
      word: '',
      synonym: ''
    };
  } 
 handleSubmit = (event) => {
    event.preventDefault();
    const user = {
      word: this.state.word
    };
    axios.put("https://jsonplaceholder.typicode.com/users", {user})
         .then(res=> {
           console.log(res);
           console.log(res.data);
         })
  };

    componentDidMount() {
        axios.get("https://jsonplaceholder.typicode.com/users" ).then((res)=> {      
          console.log(res);
            this.setState({
              persons: res.data
            });
        });
   }
  handleInputChange = (event) => {
    event.preventDefault()
      this.setState({
        [event.target.name] : event.target.value
      });
  };
 
  render() {
    const { word, synonym} = this.state
    return (
      <div className="main-div">
            <Card style={{ width: '55rem' }} id="prva">
              <Card.Body>
                {this.state.persons.map(person=> <li key= {person.id}> {person.name} </li>)}
                <Card.Title id="naslov"> Add your synonym </Card.Title>
                  <Form onSubmit= {this.handleSubmit}  id="forma" >
                    <Form.Text> Add a word</Form.Text>
                    <input type="text1" placeholder="Enter a word" id="bar" name= "word" value={word} onChange= {this.handleInputChange}/>
                    <Form.Text> Add a synonym</Form.Text>
                    <input type="text2" placeholder="Enter synonyms" id="bar" name= "synonym" value={synonym} onChange= {this.handleInputChange}/>
                    <Button type="submit" variant="outline-warning" id="save" >Save</Button>
                  </Form>
              </Card.Body>
            </Card>
      </div>
    );
  }
}

export default AddSynonym;
