import React from 'react';
import './stil.css';
import { Button, Form,  Card} from "react-bootstrap";
import axios from 'axios';
class AddSynonym extends React.Component {
  constructor(props){
    super(props);
    this.state={
      word: '',
      synonym: '',
      isFetching: false,
      opis: ''
    };
  }
 
 handleSubmit = (event) => {
        event.preventDefault();

        this.setState({
            isFetching: true
        });

        const form = event.currentTarget;

        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
            this.setState({
                isFetching: false
            });
        }
        else {
            if(this.state.word !== null && this.state.synonym != null) {
                let reqBody = {
                    word: this.word.current.value,
                    synonym: this.synonym.current.value
                };
                    
                axios.post('http://localhost:5000/sinonimi', reqBody)
                .then(() => {
                    this.setState({
                        isFetching: false,
                        opis: "Synonym is successuly added!",
                      });
                    this.word.current.value = null;
                    this.synonym.current.value = null;
                })
                .catch(()=> {
                    this.setState({
                        isFetching: false,
                        naslovGreska: "Error!"
                    });
                });
            }    
       }
    }

    componentDidMount() {
        this.setState({
            isFetching: true,
            greskaBaza: 3
        });

        const predmet = window.localStorage.getItem("idPredmeta");
        const idPredmeta = predmet !== null ? predmet : 64
        axios.get("http://localhost:31906/api/fox/ispiti/" + idPredmeta).then((res)=> {
            this.setState({
                ispiti: res.data,
                isFetching: false,
                greskaBaza: 0
            });
        })
        .catch(()=> {
            this.setState({
                greskaBaza: 1,
                isFetching: false,
                naslovGreska: "Ispiti nisu učitani!",
                opisGreska: "Baza podataka nije dostupna."
            });
        });
    }

  handleInputChange = (event) => {
    event.preventDefault()
      this.setState({
        [event.target.name] : event.target.value
      });
  };
 


  render() {
    const { word, synonym} = this.state
    return (
      <div className="main-div">
            <Card style={{ width: '55rem' }} id="prva">
              <Card.Body>
                <Card.Title id="naslov"> Add your synonym </Card.Title>
                
                  <Form onSubmit= {this.handleSubmit}  id="forma" >
                    <Form.Text> Add a word</Form.Text>
                    <input type="text1" placeholder="Enter a word" id="bar" name= "word" value={word} onChange= {this.handleInputChange}/>
                    <Form.Text> Add a synonym</Form.Text>
                    <input type="text2" placeholder="Enter synonyms" id="bar" name= "synonym" value={synonym} onChange= {this.handleInputChange}/>
                    <Button type="submit" variant="outline-warning" id="save" >Save</Button>
                  </Form>

              </Card.Body>
            </Card>
      </div>
    );
  }
}

export default AddSynonym;
