import React from 'react';
import './stil.css';
import { Button, Form,  Card} from "react-bootstrap";
import axios from 'axios';
class AddSynonym extends React.Component {
  constructor(props){
    super(props);
    this.state={
      tekst : "Not yet",
      word: '',
      synonym: ''
    };
  }
  handleSubmit = (event) => {
    event.preventDefault()
      console.log("Final data is", this.state)
  };
  handleInputChange = (event) => {
    event.preventDefault()
      this.setState({
        [event.target.name] : event.target.value
      });
  };
  handleButtonClick = () => {
  axios.get("/new").then(response => {
    this.setState({
      tekst:response.data
    });
  });
};


  render() {
    return (
      <div className="main-div">
            <Card style={{ width: '55rem' }} id="prva">
              <Card.Body>
                <Card.Title id="naslov"> Add your synonym </Card.Title>
                
                  <Form id="forma" onSubmit= {this.handleSubmit}>
                    <Form.Text> Add a word</Form.Text>
                    <input type="text1" placeholder="Enter a word" id="bar" name= "word" onChange= {this.handleInputChange}/>
                    <Form.Text> Add a synonym</Form.Text>
                    <input type="text2" placeholder="Enter synonyms" id="bar" name= "synonym"/>
                    <p> Data is: {this.state.word} {this.state.synonym}</p>
                    <Button variant="outline-warning" id="save" >Save</Button>
                  </Form>
              </Card.Body>
            </Card>
      </div>
    );
  }
}

export default AddSynonym;
