import React from 'react';
import './stil.css';
import { Button, Form, FormControl, Card} from "react-bootstrap";
import axios from "axios";
class Home extends React.Component {

  constructor(props){
    super(props);
    this.state={
      searchbar: '',
      opis: '',
      uspjeh: ''
    };
  } 
  handleInputChange = (event) => {
    event.preventDefault()
      this.setState({
        [event.target.name] : event.target.value
      });
  };
 
 
  handleButtonClick = (e) => {
    e.preventDefault();
    axios.get("http://localhost:5000/sinonimi" + this.state.searchbar).then((res)=> {
      alert("Pronadjen");
        this.setState({
            opis: "Pronaen.",
            uspjeh: res.data.synonym
        });
    })
    .catch(()=> {
        this.setState({ 
            opis: "Nije pronaen."
        });
    });
};

   render() {
    return (
      <div className="main-div">
            <Card style={{ width: '55rem' }} id="prva">
              <Card.Body>
                <Card.Title id="naslov"> Welcome to the  <br></br>synonyms search tool </Card.Title>
                <Card.Text id="tekst">
                  Search synonyms
                  <Form>
                    <input type="text" placeholder="Enter a word" id="bar" name= "searchbar" value={searchbar} onChange= {this.handleInputChange} />
                    <Button onClick={this.handleButtonClick} variant="outline-warning">Search</Button>
                    <h1>   opis={this.state.opis}</h1>
                    <h2>   uspjeh={this.state.uspjeh}</h2>
                  </Form>
                  <Button href="/addsynonym"variant="outline-warning" id="add"> Add your synonyms </Button>
                </Card.Text>
              </Card.Body>
            </Card>
      </div>
    );
  }
}

export default Home;
