import React from 'react';
import './stil.css';
import { Button, Form, FormControl, Card} from "react-bootstrap";
import axios from "axios";
class Home extends React.Component {

  constructor(){
    super();
    this.state={
      tekst : "Not yet"
    };
  }
 
  handleButtonClick = () => {
  axios.get("/new").then(response => {
    this.setState({
      tekst:response.data
    });
  });
};

   render() {
    return (
      <div className="main-div">
            <Card style={{ width: '55rem' }} id="prva">
              <Card.Body>
                <Card.Title id="naslov"> Welcome to the  <br></br>synonyms search tool </Card.Title>
                <Card.Text id="tekst">
                  Search synonyms
                  <Form>
                    <FormControl type="text" placeholder="Enter a word" id="bar" />
                    <Button onClick={this.handleButtonClick} variant="outline-warning">Search</Button>
                  </Form>
                  <Button href="/addsynonym"variant="outline-warning" id="add"> Add your synonyms </Button>
                </Card.Text>
              </Card.Body>
            </Card>
      </div>
    );
  }
}

export default Home;
