  constructor(props){
    super(props);
    this.state={
      ssearchbar: '',
      opis: '',
      uspjeh: ''
    };
  } 

  handleButtonClick = (e) => {
    e.preventDefault();
    axios.get("http://localhost:5000/sinonimi" + this.state.searchbar).then((res)=> {
      alert("Pronadjen");
        this.setState({
            opis: "Pronađen.",
            uspjeh: res.data.synonym
        });
    })
    .catch(()=> {
        this.setState({ 
            opis: "Nije pronađen."
        });
    });
};
handleInputChange = (event) => {
  event.preventDefault()
    this.setState({
      [event.target.name] : event.target.value
    });
};

